import React from "react";

class Todo extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: props.item };
  }
  render() {
    return (
      <div className='Todo'>
        <input
          type='checkbox'
          id={this.state.item.id}
          name={this.state.item.id}
          checked={this.state.item.done}
        />
        <label htmlFor='todo0'>Todo 컴포넌트 만들기</label>
      </div>
    );
  }
}

export default Todo;
