import "./App.css";
import React from "react";
import Todo from "./Todo";
import AddTodo from "./AddTodo";
import { Paper, List, Container } from "@mui/material";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: "0", title: "책 다 읽기", done: true },
        { id: "1", title: "강의 다 보기", done: false },
      ],
    };
  }

  add = (item) => {
    const thisItems = this.state.items;
    item.id = "ID-" + thisItems.length; //key를 위한 id 추가
    item.done = false; //done 초기화
    thisItems.push(item); //리스트에 아이템 추가
    this.setState({ items: thisItems }); //업데이트는 반드시 this.setState로 해야 됨
    console.log("item:" + this.state.items);
  };

  delete = (item) => {
    const thisItem = this.state.items;
    thisItem.pop(item);
    this.setState({ items: thisItem });
  };
  render() {
    const todoItems = this.state.items.length > 0 && (
      <Paper style={{ margin: 16 }}>
        <List>
          {this.state.items.map((item, idx) => (
            <Todo item={item} key={item.id} />
          ))}
        </List>
      </Paper>
    );
    return (
      <div className='App'>
        <Container maxwidth='md'>
          <AddTodo add={this.add} />
          <div className='TodoList'>{todoItems}</div>;
        </Container>
      </div>
    );
  }
}
export default App;
