import "./App.css";
import React from "react";
import Todo from "./Todo";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        { id: "0", title: "책 다 읽기", done: true },
        { id: "1", title: "강의 다 보기", done: false },
      ],
    };
  }
  render() {
    return (
      <div className='App'>
        const todoItems = this.state.items.map((item, index)=>(
        <Todo item={item} key={item.id} />
        ));
      </div>
    );
  }
}
export default App;
