import React from "react";
//import { ListItem, ListItemText, InputBase, Checkbox } from "@material-ui/core";
import {
  ListItem,
  ListItemText,
  InputBase,
  Checkbox,
  ListItemSecondaryAction,
  IconButton,
} from "@mui/material";
import { DeleteOutlined } from "@mui/icons/DeleteOutlined";
class Todo extends React.Component {
  constructor(props) {
    super(props);
    this.state = { item: props.item };
  }
  render() {
    const item = this.state.item;
    return (
      <ListItem>
        <Checkbox checked={item.done} />
        <ListItemText>
          <div className='Todo'>
            <InputBase
              inputProps={{ "arial-label": "naked" }}
              type='text'
              id={item.id}
              name={item.id}
              value={item.title}
              multiline={true}
              fullWidth={true}
            />
          </div>
          <ListItemSecondaryAction>
            <IconButton aria-label='Delete Todo'>
              <DeleteOutlined />
            </IconButton>
          </ListItemSecondaryAction>
        </ListItemText>
      </ListItem>
    );
  }
}

export default Todo;
